// Get email data from the Extract Email Data node
const emailData = $('Extract Email Data').first().json;

// Get AI response from current input
const aiResponse = $input.first().json;

// Parse AI analysis
let aiAnalysis = {};
try {
  aiAnalysis = JSON.parse(aiResponse.response);
} catch (error) {
  aiAnalysis = { error: 'Failed to parse' };
}

const result = {
  email: {
    id: emailData.id,
    threadId: emailData.threadId,
    subject: emailData.subject,
    from: emailData.from,
    to: emailData.to,
    snippet: emailData.snippet,
    processedAt: emailData.processedAt
  },
  analysis: aiAnalysis,
  processing: {
    time_ms: Math.round(aiResponse.total_duration / 1000000),
    model: aiResponse.model,
    completed_at: new Date().toISOString()
  }
};

return result;

// No email found in the specified label
const result = {
  error: 'No emails found in --watch/vc or --watch-vc label',
  searchedLabels: ['--watch/vc', '--watch-vc'],
  timestamp: new Date().toISOString(),
  processingComplete: false
};

console.log(' No emails found in VC watch folders');

return result;
